CREATE TABLE "TR" ("TR_ID" VARCHAR2(50) NOT NULL,"EMPLOYEE_ID" VARCHAR2(50) NOT NULL,"TR_DATE_CREATED" TIMESTAMP NOT NULL,"EVENT_ID" VARCHAR2(50) NOT NULL,"REQUIRES_MORE_INFO" NUMBER(1,0) NOT NULL,"REIMBURSEMENT_ID" VARCHAR2(50) NOT NULL,"APPROVAL_STATUS" NUMBER(1,0) NOT NULL,"FINAL_GRADE" VARCHAR2(50) NULL,PRIMARY KEY ("TR_ID") )NOCOMPRESS NOPARALLEL ;ALTER TABLE "TR" ADD CONSTRAINT "UNIQUE_RELATED_EVENT" UNIQUE ("EVENT_ID");ALTER TABLE "TR" ADD CONSTRAINT "UNIQUE_RELATED_REIMBURSEMENT" UNIQUE ("REIMBURSEMENT_ID");CREATE TABLE "EMPLOYEE" ("EMPLOYEE_ID" VARCHAR2(50) NOT NULL,"USERNAME" VARCHAR2(50) NOT NULL,"PASS_WORD" VARCHAR2(128) NOT NULL,"F_NAME" VARCHAR2(50) NOT NULL,"L_NAME" VARCHAR2(50) NOT NULL,"TITLE" NUMBER(1,0) NOT NULL,"BEN_CO_MEMBER" NUMBER(1,0) NOT NULL,"SUPERVISED_BY" VARCHAR2(50) NULL,PRIMARY KEY ("EMPLOYEE_ID") )NOCOMPRESS NOPARALLEL ;CREATE TABLE "TITLE" ("TITLE_ID" NUMBER(2,0) NOT NULL,"TITLE_DESCRIPTION" VARCHAR2(32) NOT NULL,PRIMARY KEY ("TITLE_ID") )NOCOMPRESS NOPARALLEL ;CREATE TABLE "REIMBURSEMENT_TYPE" ("REIMBURSEMENT_TYPE_ID" NUMBER(1,0) NOT NULL,"TR_NAME" VARCHAR2(50) NOT NULL,"PERCENTAGE" NUMBER(4,4) NOT NULL,PRIMARY KEY ("REIMBURSEMENT_TYPE_ID") )NOCOMPRESS NOPARALLEL ;CREATE TABLE "ATTATCHMENT" ("ATTATCHMENT_ID" VARCHAR2(50) NOT NULL,"ATTATCHMENT" BLOB NOT NULL,"TR_ID" VARCHAR2(50) NOT NULL,PRIMARY KEY ("ATTATCHMENT_ID") )NOCOMPRESS NOPARALLEL ;CREATE TABLE "APPROVAL_STATUS" ("APPROVAL_STATUS" NUMBER(1,0) NOT NULL,"APPROVAL_TYPE" VARCHAR2(50) NOT NULL,PRIMARY KEY ("APPROVAL_STATUS") )NOCOMPRESS NOPARALLEL ;CREATE TABLE "TR_USER_APPROVAL_STATUS" ("TUAS_ID" VARCHAR2(50) NOT NULL,"TR_ID" VARCHAR2(50) NOT NULL,"APPROVER_ID" VARCHAR2(50) NOT NULL,"STATUS_ID" NUMBER(1,0) NOT NULL,"TIME_APPROVED" TIMESTAMP NOT NULL,PRIMARY KEY ("TUAS_ID") )NOCOMPRESS NOPARALLEL ;CREATE TABLE "EVENT" ("EVENT_ID" VARCHAR2(50) NOT NULL,"DATE_OF_EVENT" DATE NOT NULL,"LOCATION" VARCHAR2(100) NOT NULL,"DESCRIPTION" VARCHAR2(4000) NOT NULL,"MINIMUM_PASSING_GRADE" NUMBER(3,2) NULL,"GRADING_REQUIRED" NUMBER(1,0) NOT NULL,PRIMARY KEY ("EVENT_ID") )NOCOMPRESS NOPARALLEL ;CREATE TABLE "PAST_CLAIM" ("CLAIM_ID" VARCHAR2(50) NOT NULL,"EMPLOYEE_ID" VARCHAR2(50) NOT NULL,"AMMOUNT_SUCCESSFULLY_REIMBURSE" NUMBER(19,4) NOT NULL,"DATE_REIMBURSED" DATE NOT NULL,"TR_ID" VARCHAR2(50) NULL,PRIMARY KEY ("CLAIM_ID") )NOCOMPRESS NOPARALLEL ;CREATE TABLE "REIMBURSEMENT" ("REIMBURSEMENT_ID" VARCHAR2(50) NOT NULL,"REIMBURSEMENT_TYPE_ID" NUMBER(1,0) NOT NULL,"REIMBURSEMENT_REQUESTED" NUMBER(19,4) NOT NULL,"FINAL_REIMBURSEMENT_AMMOUNT" NUMBER(19,4) NOT NULL,"REASON" VARCHAR2(4000) NULL,PRIMARY KEY ("REIMBURSEMENT_ID") )NOCOMPRESS NOPARALLEL ;CREATE TABLE "LOG" ("LOG_ID" VARCHAR2(50) NOT NULL,"LOG_DATETIME" TIMESTAMP NULL,"LOG_TEXT" VARCHAR2(4000) NOT NULL,PRIMARY KEY ("LOG_ID") )NOCOMPRESS NOPARALLEL ;ALTER TABLE "EMPLOYEE" ADD CONSTRAINT "SUPERVISOR_CNSTR" FOREIGN KEY ("SUPERVISED_BY") REFERENCES "EMPLOYEE" ("EMPLOYEE_ID");ALTER TABLE "EMPLOYEE" ADD CONSTRAINT "TITLE_CONSTR" FOREIGN KEY ("TITLE") REFERENCES "TITLE" ("TITLE_ID");ALTER TABLE "TR" ADD CONSTRAINT "TR_USER_CONSTR" FOREIGN KEY ("EMPLOYEE_ID") REFERENCES "EMPLOYEE" ("EMPLOYEE_ID");ALTER TABLE "TR" ADD CONSTRAINT "TR_EVENTID_CONSTR" FOREIGN KEY ("EVENT_ID") REFERENCES "EVENT" ("EVENT_ID");ALTER TABLE "PAST_CLAIM" ADD CONSTRAINT "PAST_CLAIM_EMPL_CONST" FOREIGN KEY ("EMPLOYEE_ID") REFERENCES "EMPLOYEE" ("EMPLOYEE_ID");ALTER TABLE "REIMBURSEMENT" ADD CONSTRAINT "R_ID_R_TYPE_CONST" FOREIGN KEY ("REIMBURSEMENT_TYPE_ID") REFERENCES "REIMBURSEMENT_TYPE" ("REIMBURSEMENT_TYPE_ID");ALTER TABLE "TR" ADD CONSTRAINT "TR_R_ID_CONSTR" FOREIGN KEY ("REIMBURSEMENT_ID") REFERENCES "REIMBURSEMENT" ("REIMBURSEMENT_ID");ALTER TABLE "TR" ADD CONSTRAINT "TR_APPR_TYPE_CONSTR" FOREIGN KEY ("APPROVAL_STATUS") REFERENCES "APPROVAL_STATUS" ("APPROVAL_STATUS");ALTER TABLE "TR_USER_APPROVAL_STATUS" ADD CONSTRAINT "TR_RECORD_APPR_STAT_CONSTR" FOREIGN KEY ("TR_ID") REFERENCES "TR" ("TR_ID");ALTER TABLE "ATTATCHMENT" ADD CONSTRAINT "TR_ATTATCH_CONST" FOREIGN KEY ("TR_ID") REFERENCES "TR" ("TR_ID");ALTER TABLE "PAST_CLAIM" ADD CONSTRAINT "TR_ID_RELATED_TO_CLAIM" FOREIGN KEY ("TR_ID") REFERENCES "TR" ("TR_ID");commit;